--
-- Structure de la table `t_commentaire`
--

CREATE TABLE IF NOT EXISTS `t_commentaire` (
  `COM_ID` int(11) NOT NULL AUTO_INCREMENT,
  `COM_DATE` datetime NOT NULL,
  `COM_AUTEUR` varchar(100) NOT NULL,
  `COM_CONTENU` text NOT NULL,
  `BIL_ID` int(11) NOT NULL,
  `COM_PARENT` int(11) NOT NULL,
  `COM_NIVEAU` int(11) NOT NULL,
  `COM_SIGNAL` int(11) NOT NULL,
  PRIMARY KEY (`COM_ID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=15 ;

--
-- Contenu de la table `t_commentaire`
--

INSERT INTO `t_commentaire` (`COM_ID`, `COM_DATE`, `COM_AUTEUR`, `COM_CONTENU`, `BIL_ID`, `COM_PARENT`, `COM_NIVEAU`, `COM_SIGNAL`) VALUES
(1, '2017-04-26 09:55:47', 'Jean', 'Très bon résumé avec des citations, mais il manque de détails sur le personnage de Salamano, le voisin qui bat son chien, même si ce n''est pas l''essentiel de l''histoire.', 1, 1, 0, 0),
(2, '2017-04-26 09:56:54', 'Le fou du roi', 'Effectivement. Mais là ne réside pas l''essentiel.', 1, 1, 1, 0),
(3, '2017-04-26 10:08:28', 'Agnès', 'Le résumé est excellent.  J''approuve l''explication que donne l''auteur du résumé : &quot;Il prend aussi conscience que son indifférence au monde est corrélée par l’indifférence du monde à son égard. Comme tout humain, Meursault est né, mourra, et n''aura plus d''importance&quot;. Mais j''aime à penser que l´homme sage est celui qui a la capacité à contrôler ses actes, à se maîtriser soi-même. Le monde serait trop &quot;désespérant&quot; avec un personnage, tel que Meursault, insensible et ne se souciant de rien ni de personne.', 1, 2, 0, 0),
(4, '2017-04-26 10:13:50', 'Coralie', 'Très beau résumé. Je vous remercie pour ce moment de lecture.', 2, 1, 0, 0),
(5, '2017-04-26 10:18:00', 'Juju', 'Super ! Ca m''évite de lire le livre et j''ai tout compris.', 2, 2, 0, 0),
(6, '2017-04-26 10:20:12', 'Jean Forteroche', 'Ce qui est bien dommage...ce n''est pas fait pour faire vos devoirs ni pour  vous dispenser de lire les livres...au contraire, cela devrait vous donner envie de lire et de découvrir les textes pas vous-mêmes. Mais enfin si vous avez compris de quoi parle Molière, c''est déjà quelque chose...', 2, 2, 1, 0),
(7, '2017-04-26 10:24:27', 'Un lecteur', 'Merci beaucoup pour se resume de Phèdre, ça m'' a permis de mieux comprendre l''œuvre.\r\nMerci infiniment', 3, 1, 0, 0),
(8, '2017-04-26 10:31:03', 'Joan', 'Ne faudrait-il pas lire :\r\nLe Paradis est divisé en neuf sphères dont la révolution autour de la sphère Terre\r\nau lieu de :\r\nLe Paradis est divisé en neuf sphères dont la révolution autour de la s''opère Terre ?', 4, 1, 0, 0),
(9, '2017-04-26 10:37:54', 'Arthur', 'Bon résumé sur la nature de chaque personnage, pas vraiment sur l''histoire elle-même, mais au moins il n''y a pas de fautes d’orthographe...', 5, 1, 0, 0),
(10, '2017-04-26 10:39:32', 'Le raleur de service', 'Plutôt bien écrit mais je suis désolé, j''appelle pas vraiment ça un résumé !', 5, 2, 0, 0),
